<?phpglobal $rcl_options;global $wpdb;require_once(ABSPATH . 'wp-admin/includes/upgrade.php');	$table10 = RCL_PREF ."groups_options";	if($wpdb->get_var("show tables like '". $table10 . "'") != $table10) {		$wpdb->query("CREATE TABLE IF NOT EXISTS `". $table10 . "` (		  ID bigint (20) NOT NULL AUTO_INCREMENT,		  group_id INT(20) NOT NULL,		  option_value LONGTEXT NOT NULL,	  		  UNIQUE KEY id (id)		) DEFAULT CHARSET=utf8;");		}else{		if($wpdb->get_var("SELECT DATA_TYPE FROM information_schema.COLUMNS WHERE TABLE_NAME = '".$table10."' AND COLUMN_NAME = 'option_value'") == 'varchar'){			$wpdb->query("ALTER TABLE `".$table10."` MODIFY `option_value` LONGTEXT");		}	}$rcl_options['user_groups_recall']='';update_option('primary-rcl-options',$rcl_options);?>